def sort_files(files):
    sorted_files = []
    for file in files:
        min_index = len(sorted_files)
        file_name = file["filename"]
        for i, sorted_file in enumerate(sorted_files):
            if file_name < sorted_file["filename"]:
                min_index = i
                break
        sorted_files.insert(min_index, file)
    return sorted_files
